import React from 'react';
import StripeCheckout from 'react-stripe-checkout';

import axios from 'axios';

const StripeCheckoutButton = ({ price }) => {
    const priceForStripe = price * 100;
    const publishableKey = 'pk_test_51HItnVFin6ubk68OIPBBVezdNk2XWOzP6XQc1TEglTOjlls7BZnFkqFIIHRpsJAGpXnGcQPnsP04d5DLrcYqsrO000kruFuNkj';

    const onToken = token => {
        axios({
            url: 'payment',
            method: 'post',
            data: {
                amount: priceForStripe,
                token
            }
        })
        .then(response => {
            alert('Payment successful!')
        }).catch(error => {
            console.log('Payment error: ', JSON.parse(error));
            alert('There are an issue with your payment. Please sure you use the provided credit card');
        });
    }

    return (
        <StripeCheckout 
            label='Pay Now'
            name='CRWN Clothing Ltd.'
            billingAddress
            shippingAddress
            image='https://svgshare.com/i/CUz.svg'
            description={`Your total is $${price}`}
            amount={priceForStripe}
            panelLabel='Pay Now'
            token={onToken}
            allowRememberMe
            stripeKey={publishableKey}
        />
    );
};

export default StripeCheckoutButton;
